pragma solidity =0.8.4;

library Utils { 

      function StringConcat(bytes memory _base, bytes memory _value) internal pure returns (string memory) {
        string memory _tmpValue = new string(_base.length + _value.length);
        bytes memory _newValue = bytes(_tmpValue);

        uint i;
        uint j;

        for(i=0; i<_base.length; i++) {
            _newValue[j++] = _base[i];
        }

        for(i=0; i<_value.length; i++) {
            _newValue[j++] = _value[i++];
        }

        return string(_newValue);
    }

    function Uint8Sub(uint8 a, uint8 b) internal pure returns (uint8) {
        assert(b <= a);
        return a - b;
    }


}","keccak256":"0xbf6fa1647a1adccfe3d98608754aa1c15f5910299b6d48798f6931874d451ee9","license":"MIT"},"contracts/libraries/Vault.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity =0.8.4;


import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol"; 
import {SafeMath} from "@openzeppelin/contracts/utils/math/SafeMath.sol";
import './Utils.sol';

library Vault { 

    using SafeMath for uint256;

    struct UserInfo {
        uint256 pendingAmount;  
        uint256 ongoingAmount;   
        uint256 requestingAmount;
        uint256 maturedAmount; 
        uint256 pendingPKKTReward;  
        uint256 rewardDebt; // Reward debt. See explanation below.
        uint256 pendingReward;// Reward but not harvest
        //
        //   pending reward = (user.amount * pool.accPKKTPerShare) - user.rewardDebt
        //
        // Whenever a user deposits or withdraws LP tokens to a pool. Here's what happens:
        //   1. The pool's `accPKKTPerShare` (and `lastRewardBlock`) gets updated.
        //   2. User receives the pending reward sent to his/her address.
        //   3. User's `amount` gets updated.
        //   4. User's `rewardDebt` gets updated.
        bool hasDeposit;
    } 

    struct VaultInfo {   
        uint256 lastRewardBlock; // Last block number that PKKTs distribution occurs.
        uint256 accPKKTPerShare; // Accumulated PKKTs per share, times 1e12. See below.
        uint256 totalPending;
        uint256 totalOngoing;
        uint256 totalRequesting;
        uint256 totalMatured;

        // the underlying token: usdt/usdc/dai/etc.
        IERC20 underlying;
        uint8 decimals;
    }

    function getShare(VaultInfo storage _vault, uint8 _maxDecimals) external view returns(uint256) {
        uint8 extraDecimals = Utils.Uint8Sub(_maxDecimals, _vault.decimals);
        if (extraDecimals > 0) {
            return _vault.totalOngoing.mul(10 ** uint256(extraDecimals));
        }
        else {
            return _vault.totalOngoing;
        }
    }


    struct VaultSettings {

        IERC20 underlying; 
        uint8 decimals;
    }


}
